import addCardButton from "../images/Vector.svg";
import editAvatar from "../images/profile-avatar-edit.svg";
import '../index.css';

function Main() {
	return (
		<>
			<main className="content">
            <section className="profile">
              <div className="profile__avatar-block" onClick={this.handleEditAvatarClick}>
                <img
                  className="profile__avatar-edit"
                  src={editAvatar}
                  alt="edit avatar"
                />
                <img src="#" alt="" className="profile__avatar" />
              </div>
              <div className="profile__info">
                <div classname="profile__block">
                  <h1 className="profile__name"></h1>
                  <button className="profile__edit-button" type="button" onClick={this.handleEditProfileClick}></button>
                </div>
                <p className="profile__about"></p>
              </div>
              <button className="profile__add-button" type="button" onClick={this.handleAddPlaceClick}>
                <img
                  src={addCardButton}
                  alt="кнопка добавления изображений"
                  className="profile__button-img"
                />
              </button>
            </section>
            <ul className="elements">
              <template id="template-element">
                <li className="element">
                  <img className="element__image" src="#" />
                  <button className="element__delete-button" type="button"></button>
                  <div className="element__block">
                    <h2 className="element__title"></h2>
                    <div className="element__like-block">
                      <button className="element__like" type="button"></button>
                      <p className="element__like-counter"></p>
                    </div>
                  </div>
                </li>
              </template>
            </ul>
          </main>
		</>
	)

}

handleEditAvatarClick = () => {
	const popup = document.querySelector('.popup_type_avatar');
	popup.classList.add('popup_opened');
 }

 handleEditProfileClick = () => {
	const popup = document.querySelector('.popup_type_profile');
	popup.classList.add('popup_opened');
 }

 handleAddPlaceClick = () => {
	const popup = document.querySelector('.popup_type_element');
	popup.classList.add('popup_opened');
 }


export default Main
