import React, { useState } from 'react';
import { Link, useNAvigate } from 'react-router-dom';
import { register } from '../utils/Auth';

function Register({ onRegister }) {
  const navigate = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    register(email, password)
      .then(() => {
        setIsLoading(false);
        onRegister(true); // Вызываем колбэк-функцию, чтобы передать информацию об успешной регистрации
        history.push('/sign-in'); // Перенаправляем пользователя на страницу входа
      })
      .catch((error) => {
        setIsLoading(false);
        setError('Ошибка регистрации');
      });
  };

  return (
    <div className="register">
      <h2 className="register__title">Регистрация</h2>
      <form className="register__form" onSubmit={handleSubmit}>
        <input
          className="register__input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={handleEmailChange}
          required
        />
        <input
          className="register__input"
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <button className="register__submit-button" type="submit" disabled={isLoading}>
          {isLoading ? 'Регистрация...' : 'Зарегистрироваться'}
        </button>
      </form>
      {error && <p>{error}</p>}
      <p className="register__text">
        Уже зарегистрированы? <Link to="/sign-in" className="register__link">Войти</Link>
      </p>
    </div>
  );
}

export default Register;




