import React, { useEffect, useState } from "react";
// import Card from "./Card";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import api from "../utils/Api";
import addCardButton from "../images/Vector.svg";
import editAvatar from "../images/profile-avatar-edit.svg";

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick, onCardLike, onCardDelete, }) {
  const [cards, setCards] = useState([]);
  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");


  // useEffect(() => {
  //   // Получение пользовательских данных
  //   api.getUserInfo()
  //     .then((data) => {
  //       setUserName(data.name);
  //       setUserDescription(data.about);
  //       setUserAvatar(data.avatar);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });

  //   // Получение списка карточек
  //   api.getInitialCards()
  //     .then((data) => {
  //       setCards(data);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }, []);

  // Остальной JSX код компонента


  // const currentUser = React.useContext(CurrentUserContext);

  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([userInfo, initialCards]) => {
        setUserName(userInfo.name);
        setUserDescription(userInfo.about);
        setUserAvatar(userInfo.avatar);
        setCards(initialCards);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  // function handleCardClick(card) {
  //   props.onCardClick(card);
  // }

  return (
    <>
      <main className="content">
        <section className="profile">
          <div className="profile__avatar-block" onClick={onEditAvatar}>
            <img
              className="profile__avatar-edit"
              src={editAvatar}
              alt="edit avatar"
            />
            <img style={{ backgroundImage: `url(${userAvatar})` }} src={userAvatar} alt="" className="profile__avatar" />
          </div>
          <div className="profile__info">
            <div className="profile__block">
              <h1 className="profile__name">{userName}</h1>
              <button
                className="profile__edit-button"
                type="button"
                onClick={onEditProfile}
              ></button>
            </div>
            <p className="profile__about">{userDescription}</p>
          </div>
          <button
            className="profile__add-button"
            type="button"
            onClick={onAddPlace}
          >
            <img
              src={addCardButton}
              alt="кнопка добавления изображений"
              className="profile__button-img"
            />
          </button>
        </section>
        <section className="elements">
          {cards.map((card) => (
            <li key={card._id} className="element">
              <img className="element__image" src={card.link} alt={card.name} />
              <button className="element__delete-button" type="button"></button>
              <div className="element__block">
                <h2 className="element__title">{card.name}</h2>
                <div className="element__like-block">
                  <button className="element__like" type="button"></button>
                  <p className="element__like-counter">{card.likes.length}</p>
                </div>
              </div>
            </li>
          ))}
        </section>
        {/* <section className="elements">
          {cards.map((card) => (
            <Card key={card._id} card={card} onCardClick={handleCardClick} />
          ))}
        </section> */}
        {/* <ul className="elements">
              <template id="template-element">
                <li className="element">
                  <img className="element__image" src="#" />
                  <button className="element__delete-button" type="button"></button>
                  <div className="element__block">
                    <h2 className="element__title"></h2>
                    <div className="element__like-block">
                      <button className="element__like" type="button"></button>
                      <p className="element__like-counter"></p>
                    </div>
                  </div>
                </li>
              </template>
            </ul> */}
      </main>
    </>
  );
}

export default Main;
