import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { login } from '../utils/Auth';

function Login({ onAuth }) {
  const history = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    login(email, password)
      .then((data) => {
        setIsLoading(false);
        onAuth(true); // Вызываем колбэк-функцию, чтобы передать информацию об успешной авторизации
        history.push('/'); // Перенаправляем пользователя на главную страницу
      })
      .catch((error) => {
        setIsLoading(false);
        setError('Ошибка авторизации');
      });
  };

  return (
    <div className="login">
      <h2 className="login__title">Вход</h2>
      <form className="login__form" onSubmit={handleSubmit}>
        <input
          className="login__input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={handleEmailChange}
          required
        />
        <input
          className="login__input"
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <button className="login__submit-button" type="submit" disabled={isLoading}>
          {isLoading ? 'Вход...' : 'Войти'}
        </button>
      </form>
      {error && <p>{error}</p>}
      <p className="register__text">
        Нет аккаунта? <Link to="/sign-up" className="register__link">Зарегистрируйтесь</Link>
      </p>
    </div>
  );
}

export default Login;



