import React, { useEffect, useState, useContext } from "react";
import Card from "./Card";
import api from "../utils/Api";
import addCardButton from "../images/Vector.svg";
import editAvatar from "../images/profile-avatar-edit.svg";
import CurrentUserContext from '../contexts/CurrentUserContext';

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick, onCardLike, onCardDelete, cards }) {

  const currentUser = useContext(CurrentUserContext);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar-block" onClick={onEditAvatar}>
          <img
            className="profile__avatar-edit"
            src={editAvatar}
            alt="edit avatar"
          />
          <img
            style={{ backgroundImage: `url(${currentUser.avatar})` }}
            src={currentUser.avatar}
            alt=""
            className="profile__avatar"
          />
        </div>
        <div className="profile__info">
          <div className="profile__block">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button
              className="profile__edit-button"
              type="button"
              onClick={onEditProfile}
            ></button>
          </div>
          <p className="profile__about">{currentUser.about}</p>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={onAddPlace}
        >
          <img
            src={addCardButton}
            alt="кнопка добавления изображений"
            className="profile__button-img"
          />
        </button>
      </section>
      <ul className="elements">
        {cards.map((card) => (
          <Card 
          // onCardClick={onCardClick} 
          // key={card._id} 
          // card={card} 
          // onCardLike={onCardLike}
          // onCardDelete={onCardDelete} 
            onCardClick={onCardClick}
            link={card.link}
            name={card.name}
            likes={[...card.likes]}
            key={card._id}
            owner={card.owner}
            _id={crd._id}
            onCardLike={onCardLike}
            onCardDelete={onCardDelete}
          />
        ))}
      </ul>
    </main>
  );
}

export default Main;
