import React from "react";
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Header from "./Header";
import Main from "./Main";
import Login from "./Login";
import Register from "./Register";
import Footer from "./Footer";
import EditProfilePopup from "./EditProfilePopup";
import AddPlacePopup from "./AddPlacePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import ImagePopup from "./ImagePopup";
import PopupConfirm from "./PopupConfirm";
import api from "../utils/Api";
import CurrentUserContext from "../contexts/CurrentUserContext";
import ProtectedRoute from "./ProtectedRoute";
import * as Auth from "../utils/Auth"; 


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  const [isLoggedIn, setIsLoggedIn] = React.useState(false);

  const [isOpenTooltip, setIsOpenTooltip] = React.useState(false);
  const [titleTooltip, setTitleTooltip] = React.useState("");
  const [iconTooltip, setIconTooltip] = React.useState("");

  const [email, setEmail] = React.useState(null);

  // Записываем функцию useNavigate в переменную navigate
  const navigate = useNavigate();


  React.useEffect(() => {
    if (!isLoggedIn) return;
    api
      .getUserInfo()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => console.log(err));

    api
      .getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => console.log(err));
  }, [isLoggedIn]);


 React.useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        setCurrentUser(data)
      })
      .catch((err) => console.log(err));
  }, []);

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((res) => {
        setCards([...res]);
      })
      .catch((err) => console.log(err));
  }, []);


  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  const handleCardClick = (card) => {
    setSelectedCard(card);
  };

  const handleCardLike = (card) => {
    // Проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
      })
      .catch((error) => {
        console.log('Ошибка при обновлении статуса лайка:', error);
      });
  };


  const handleCardDelete = (card) => {
    // Отправляем запрос в API для удаления карточки
    api.deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((item) => item._id !== card._id));
      })
      .catch((error) => {
        console.log('Ошибка при удалении карточки:', error);
      });
  };

  function handleUpdateUser(data) {
    api
    .setUserInfo(data)
    .then((res) => {
      setCurrentUser(res);
      closeAllPopups();
    })
    .catch((err) => console.log(err))
  }

  function handleUpdateAvatar(data) {
    api
    .setAvatar(data)
    .then((res) => {
      setCurrentUser(res);
      closeAllPopups();
    })
    .catch((err) => console.log(err))
  }

  function handleAddPlaceSubmit(data) {
    api
    .addCard(data)
    .then((newCard) => {
      setCards([newCard, ...cards]);
      closeAllPopups();
    })
    .catch((err) => console.log(err))
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  const handleLogin = (email, password) => {
    Auth
      .login(email, password)
      .then((res) => {
          localStorage.setItem("jwt", res.token); // Сохраняем токен('ключ', 'значение')
          setIsLogged(true); // Обновляем стейт локально
          setEmail(email); //Обновляем стейт почты
          navigate("/"); // Перенаправляем
      })
      .catch((err) => {
        console.log(err);
        handleInfoTooltip();
        setTitleTooltip("Что-то пошло не так! Попробуйте еще раз."); // Обновили стейт татйла
        setIconTooltip(reject); // Обновили стейт иконки
      });
  };

  const handleRegister = (email, password) => {
    Auth
      .register(email, password)
      .then(() => {
        setTitleTooltip("Вы успешно зарегистрировались!"); // Обновили стейт тайтлТултип
        setIconTooltip(resolve); // Обновили стейт иконки
        navigate("/sign-in"); // Перенаправляем в окно авторизации
      })
      .catch((err) => {
        console.log(err);
        setTitleTooltip("Что-то пошло не так! Попробуйте еще раз."); //Обновили стейт тайтла
        setIconTooltip(reject); // Обновили стейт иконки
      })
      .finally(handleInfoTooltip)
  }

  useEffect(() => {
    const jwt = localStorage.getItem("jwt");
    if(jwt) {
      auth
        .getToken(jwt)
        .then((res) => {
          if(res) {
            setIsLoggedIn(true);
            setEmail(res.data.email);// Обновляем стейт почты
          }
        })
        .catch((err) => {
          console.log(err);
        })
    }
  }, [])

  const onSignOut = () => {
    setIsLoggedIn(false);
    setEmail(null); // Обновляем стейт почты
    navigate("/sign-in");
    localStorage.removeItem("jwt");
  };


  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="root">
        <div className="page">
          <Header onSignOut={onSignOut}/>
          <Routes>
            <Route
              path='/'
              element={
                <ProtectedRoute
                  element={Main}
                  onEditProfile={handleEditProfileClick}
                  onAddPlace={handleAddPlaceClick}
                  onEditAvatar={handleEditAvatarClick}
                  onCardClick={handleCardClick}
                  onCardLike={handleCardLike}
                  onCardDelete={handleCardDelete}
                  cards={cards}
                  isLoggedIn={isLoggedIn}
                  email={email}
                />
              }
            />
            <Route
              path='sign-up'
               element={<Register 
               onRegister={handleRegister} 
               isLoggedIn={isLoggedIn} 
                  />}
            />
            <Route path='sign-in' 
             element={<Login 
             onAuth={handleLogin} 
             isLoggedIn={isLoggedIn} 
            />} 
            />
            <Route path='*' element={isLoggedIn ? <Navigate to='/' /> : <Navigate to='/sign-in' />} />
          </Routes>
          <Footer />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onAddPlace={handleAddPlaceSubmit}
          />
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
          <PopupConfirm />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />
          <InfoTooltip isOpen={isOpenTooltip} title={titleTooltip} image={iconTooltip} onClose={closeAllPopups}/>
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
