function ImagePopup({card, onClose}) {
	return (
	  <div className={`popup popup_type_image overlay ${card ? 'popup_opened' : ''}`}>
		 <figure className="popup__figure">
			<button 
			  onClick={onClose}
			  className='popup__close popup__close-button' 
			  aria-label='закрыть' 
			  type='button'>
			</button>
			<img 
			  src={card ? card.link : '#'} 
			  className='popup__photo' 
			  alt={card ? card.name : ''} 
			/>
			<f className='popup__subtitle'>{card ? card.name : ''}</f>
			</figure>
	  </div>
	);
 }

export default ImagePopup;
